import ReactDOM from 'react-dom';
import './index.css';
function User() {
    const [userName, setUserName] = useState<string>("")
    return (
        <div>
            <p>{userName}</p>
            <input
                value={useState}
                onChange={(e) => setUserName(e.currentTarget.value)}
            />
        </div>
    )
}
